CREATE TABLE mock_user_data (
			 id INTEGER,
			 first_name TEXT,
			 gender TEXT,
			 hometown TEXT,
			 birth_date TEXT,
			 lucky_number INTEGER,
			 cash INTEGER
);

SELECT MAX(cash)
  FROM mock_user_data;

SELECT MIN(cash)
  FROM mock_user_data;

SELECT ROUND(AVG(cash))
  FROM mock_user_data;

SELECT first_name, lucky_number AS lucky_total
	FROM mock_user_data
 GROUP BY first_name
HAVING first_name LIKE "K%";

SELECT COUNT(*),
	 CASE
			WHEN cash > 200 THEN "Has more than 100 Dollars"
			WHEN cash > 100 THEN "Has around 100 Dollars"
			WHEN cash = 100 THEN "Has 100 Dollars"
			ELSE "Has less than 100 Dollars"
  	END	AS "Do_You_Have_100_Dollars"
	FROM mock_user_data
 GROUP BY Do_You_Have_100_Dollars;

SELECT first_name
	FROM mock_user_data
 WHERE first_name
	LIKE "T%"
	 AND first_name
	LIKE "%A%";

SELECT first_name
	FROM mock_user_data
 WHERE first_name
	LIKE "A%"
		OR first_name
	LIKE "T%";
